Functions.starter_code:
def string_times() -> str:
    """Return a string repeated n times."""
    new_string = ""
    for i in range(n):
        new_string += string
    return new_string

Functions.solution:
def string_times(string: str, n: int) -> str:
    """Get a string repeated n times."""
    new_string = ""
    for i in range(n):
        new_string += string
    return new_string

