def xy_balance(string: str) -> bool:
    found_y = False
    i = len(string) - 1
    while i >= 0:
        if string[i] == "y":
            found_y = True
        elif string[i] == "x":
            if not found_y:
                return False
            break
        i -= 1

    return True

